import { useParams } from "react-router-dom";
import articles from "./article-content";
const ArticlePage = () => {
    const { articleId } = useParams();
    const article = articles.find(x => x.name === articleId);
    return (
        <>
            <h1>{article.title}</h1>
            {article.content.map(p => (
                <p>{p}</p>
            ))}
        </>
    );
}

export default ArticlePage;
